import chai from 'chai';
import chaiHttp from 'chai-http';
import sinon from 'sinon';
import { app } from '../app';
import { allSchedulesMock, createdScheduleMock, messageMock, scheduleMock } from './mocks/scheduleMock';
import ScheduleModel from '../models/ScheduleModel';
import { Response } from 'superagent';
import HttpError from '../utils/HttpError';
import { StatusCodes } from 'http-status-codes';

chai.use(chaiHttp);

const { expect } = chai;

describe('Testando rota /schedule', function () {
  describe('Deveria retornar todos os agendamentos', function () {
    let chaiHttpResponse: Response;

    afterEach(sinon.restore);

    it('com sucesso', async () => {
      sinon
        .stub(ScheduleModel, 'findAll')
        .resolves(allSchedulesMock as ScheduleModel[]);

      chaiHttpResponse = await chai
        .request(app)
        .get('/schedule');

      expect(chaiHttpResponse.status).to.equal(200);
      expect(chaiHttpResponse.body).to.be.an('array');
    });
  });

  describe('Deveria criar um agendamento', function () {
    let chaiHttpResponse: Response;

    afterEach(sinon.restore);

    it('com sucesso', async () => {
      sinon
        .stub(ScheduleModel, 'create')
        .resolves(createdScheduleMock.scheduleCreated as ScheduleModel);

      chaiHttpResponse = await chai
        .request(app)
        .post('/schedule')
        .send({ email: createdScheduleMock.scheduleCreated.email });

      expect(chaiHttpResponse.status).to.equal(201);
      expect(chaiHttpResponse.body.message).to.deep.equal('Service scheduled successfully');
      expect(new Date(chaiHttpResponse.body.scheduleCreated.scheduleDateTime)).to.deep.equal(createdScheduleMock.scheduleCreated.scheduleDateTime);
      expect(chaiHttpResponse.body.scheduleCreated).to.have.property('id', createdScheduleMock.scheduleCreated.id);
      expect(chaiHttpResponse.body.scheduleCreated).to.have.property('email', createdScheduleMock.scheduleCreated.email);
    });

    it('erro ao passar nenhum e-mail', async () => {
      chaiHttpResponse = await chai
        .request(app)
        .post('/schedule')
        .send();

      expect(chaiHttpResponse.status).to.equal(400);
      expect(chaiHttpResponse.body.error).to.deep.equal('Email is required');
    });

    it('erro ao passar um e-mail apenas com espaços vazios', async () => {
      chaiHttpResponse = await chai
        .request(app)
        .post('/schedule')
        .send({ email: ' '});

      expect(chaiHttpResponse.status).to.equal(400);
      expect(chaiHttpResponse.body.error).to.deep.equal('Email cannot be an empty string');
    });

    it('erro ao passar um e-mail com o formato invalido', async () => {
      chaiHttpResponse = await chai
        .request(app)
        .post('/schedule')
        .send({ email: 'teste.com'});

      expect(chaiHttpResponse.status).to.equal(400);
      expect(chaiHttpResponse.body.error).to.deep.equal('Invalid email format');
    });
  });

  describe('Deveria deletar um agendamento', function () {
    let chaiHttpResponse: Response;

    afterEach(sinon.restore);

    it('com sucesso', async () => {
      const scheduleStub = sinon.stub(ScheduleModel, 'findByPk').resolves(scheduleMock as ScheduleModel);
      const deleteStub = sinon.stub(ScheduleModel, 'destroy').resolves();

      chaiHttpResponse = await chai
        .request(app)
        .delete('/schedule/1');

      expect(chaiHttpResponse.status).to.equal(200);
      expect(chaiHttpResponse.body.message).to.deep.equal('Scheduling canceled successfully');

      sinon.assert.calledOnce(scheduleStub);
      sinon.assert.calledOnce(deleteStub);
    });

    it('erro ao informar um id inexistente', async () => {
      sinon
        .stub(ScheduleModel, 'findByPk')
        .resolves();

        const id = 999;

      chaiHttpResponse = await chai
        .request(app)
        .delete(`/schedule/${id}`);

      expect(chaiHttpResponse.status).to.equal(404);
      expect(chaiHttpResponse.body).to.deep.equal(`Schedule not found with ID: ${id}`);
    });

    it('erro ao não informar um numero de id na rota', async () => {
      chaiHttpResponse = await chai
        .request(app)
        .delete('/schedule/abc');

      expect(chaiHttpResponse.status).to.equal(400);
      expect(chaiHttpResponse.body.error).to.deep.equal('ID must be a number');
    });
  });
});

import swaggerUi from 'swagger-ui-express';
import express from 'express';
import swaggerDocument from '../swagger.json';
import 'express-async-errors';
import scheduleRouter from './routes/scheduleRouter';

class App {
  public app: express.Express;

  constructor() {
    this.app = express();

    this.app.use(express.json());

    this.app.use('/schedule', scheduleRouter);

    this.app.use('/api-docs', swaggerUi.serve, swaggerUi.setup(swaggerDocument));
  }

  public start(PORT: string | number):void {
    this.app.listen(PORT, () => console.log(`Running on port ${PORT}`));
  }
}

export { App };

// Essa segunda exportação é estratégica, e a execução dos testes de cobertura depende dela
export const { app } = new App();

import { Router } from 'express';
import ScheduleController from '../controllers/ScheduleController';
import ValidateInputEmail from '../middlewares/ValidateInputEmail';
import ValidateId from '../middlewares/ValidateId';

const router = Router();

const scheduleController = new ScheduleController();

router.route('/')
  .get(scheduleController.getAllSchedule)
  .post(ValidateInputEmail.emailFields, scheduleController.createSchedule);

router.delete('/:id', ValidateId.idFields, scheduleController.deleteSchedule);

export default router;

import { Request, Response } from 'express';
import ScheduleService from '../services/ScheduleService';
import { IScheduleInput, ISchedule } from '../interfaces/ISchedule';

export default class ScheduleController {
  constructor(private _scheduleService = new ScheduleService()) {
  }

  public getAllSchedule = async (_req: Request, res: Response): Promise<Response<ISchedule>> => {
    const allSchedules = await this._scheduleService.getAllSchedule();
    return res.status(200).json(allSchedules);
  };

  public createSchedule = async (req: Request, res: Response): Promise<Response<ISchedule>> => {
    const { email }: IScheduleInput = req.body;
    const { status, message } = await this._scheduleService.createSchedule(email);
    return res.status(status).json(message);
  };

  public deleteSchedule = async (req: Request, res: Response): Promise<Response<ISchedule>> => {
    const { id } = req.params;
    const { status, message } = await this._scheduleService.deleteSchedule(Number(id));
    return res.status(status).json(message);
  };
}

import { StatusCodes } from 'http-status-codes';
import HttpError from '../utils/HttpError';
import ScheduleModel from '../models/ScheduleModel';

export default class ScheduleService {
  constructor(public _scheduleModel = ScheduleModel) {}

  public async getAllSchedule() {
    try {
      return await this._scheduleModel.findAll();
    } catch (error) {
      throw new HttpError(StatusCodes.INTERNAL_SERVER_ERROR, (error as Error).message);
    }
  }

  public async createSchedule(email: string) {
    try {
      const scheduleCreated = await this._scheduleModel.create({ email });
      const message = 'Service scheduled successfully';
      return { status: StatusCodes.CREATED, message: { message, scheduleCreated } };
    } catch (error) {
      throw new HttpError(StatusCodes.INTERNAL_SERVER_ERROR, (error as Error).message);
    }
  }

  public async deleteSchedule(id: number) {
    try {
      const schedule = await this._scheduleModel.findByPk(id);
      if (!schedule) {
        return { status: StatusCodes.NOT_FOUND, message: `Schedule not found with ID: ${id}` };
      }

      await this._scheduleModel.destroy({ where: { id } });
      const message = { message: 'Scheduling canceled successfully' };
      return { status: StatusCodes.OK, message }; // escolhi o status 200 para enviar um mensagem ao inves do 204
    } catch (error) {
      throw new HttpError(StatusCodes.INTERNAL_SERVER_ERROR, (error as Error).message);
    }
  }
}

export default class HttpError extends Error {
  constructor(
    public status: number,
    public message: string,
  ) { super(message); }
}

import { Model, DataTypes } from 'sequelize';
import db from '.';

export default class ScheduleModel extends Model {
  declare id: number;
  declare email: string;
  declare scheduleDateTime: Date;
}

ScheduleModel.init({
  id: {
    type: DataTypes.INTEGER.UNSIGNED,
    allowNull: false,
    autoIncrement: true,
    primaryKey: true,
    unique: true,
  },
  email: {
    type: DataTypes.STRING,
    allowNull: false,
  },
  scheduleDateTime: {
    type: DataTypes.DATE,
    allowNull: false,
    unique: true,
    defaultValue: DataTypes.NOW,
  },
}, {
  underscored: false,
  sequelize: db,
  modelName: 'schedules',
  timestamps: false,
});

import { Sequelize } from 'sequelize';
import * as config from '../../config/database';

export default new Sequelize(config);

import 'dotenv/config';
